call adicionaIndexing('tbl_pessoas', 'idx_pessoa_id_antigo_cliente', 'pes_cliente, pes_id_antigo', 0, 0);
call adicionaIndexing('tbl_financeiro', 'idx_fin_lcto_id_pessoa_antigo', 'mov_receber, lcto_id_pessoa_antigo, lcto_id_antigo', 0, 0);
UPDATE tbl_financeiro SET lcto_id_pessoa = (SELECT pes_id FROM tbl_pessoas WHERE pes_cliente = 1 AND pes_id_antigo = lcto_id_pessoa_antigo) WHERE lcto_id_antigo > 0 AND lcto_id_pessoa_antigo > 0 AND mov_receber = 1;
UPDATE tbl_produtos a LEFT JOIN tbl_grupos b ON (b.gru_id_antigo=a.pro_id_grupo_antigo) SET a.pro_id_grupo=b.gru_id WHERE a.pro_id_grupo_antigo> 0 AND b.gru_id> 0;
-- UPDATE tbl_produtos a LEFT JOIN tbl_pessoas b ON (b.pes_id_antigo=a.pro_id_fornecedor) SET a.pro_id_fornecedor=b.pes_id WHERE a.pro_id_fornecedor> 0 AND b.pes_id> 0;
-- UPDATE tbl_financeiro a LEFT JOIN tbl_pessoas b ON (b.pes_id_antigo = a.lcto_id_pessoa AND b.pes_transportador = 0) SET a.lcto_id_pessoa = b.pes_id WHERE b.pes_id > 0;
